def undo_item_images():
    if environment == "production":
        db.session.execute(f"TRUNCATE table {SCHEMA}.item_image RESTART IDENTITY CASCADE;")
    else:
        db.session.execute(text("DELETE FROM item_image"))

    db.session.commit()
